mapper.select:
select * from (select rownum rn, d.emp_num, emp_name, medical_subject, medical_room_location from doctor d join employee e on d.emp_num=e.emp_num
where emp_num '%'||#{searchWord}||'%' or emp_name '%'||#{searchWord}||'%' or medical_subject '%'||#{medicalSubject}||'%' order by emp_num
) where rn between #{startRow} and #{endRow}

select d.emp_num, emp_name, medical_subject, medical_room_location 
from doctor d join employee e on d.emp_num=e.emp_num
where d.emp_num=#{empNum}

mapper.update:
update doctor 
set medical_Subject=#{medicalSubject}
, medical_Room_Location=#{medicalRoomLocation}
where emp_num=#{empNum}
