mapper.select:
select * from ( select rownum rn, from employee
emp_Num like #{kind}||'%'
and
(emp_name like '%'||#{searchWord}||'%' or position like '%'||#{searchWord}||'%' or section_num like '%'||#{searchWord}||'%' or emp_id like '%'||#{searchWord}||'%') order by emp_Num
) where rn between #{startRow} and #{endRow}

mapper.update:
update employee
set emp_Name=#{empName }, emp_Phone=#{empPhone }
	
	 , emp_gender=#{empGender},  emp_Addr=#{empAddr}
	 , emp_Addr_Detail=#{empAddrDetail }, emp_Post=#{empPost }
	 , section_num=#{sectionNum}
	 , position=#{position}, emp_Email=#{empEmail }
	 , emp_birth=#{empBirth}
where emp_NUM = #{empNum}
