mapper.select:
select * from ( select rownum rn, from employee
where emp_num like '%'||#{searchWord}||'%' or emp_name like '%'||#{searchWord}||'%' or position like '%'||#{searchWord}||'%' or section_num like '%'||#{searchWord}||'%' or emp_id like '%'||#{searchWord}||'%' order by emp_Num
) where rn between #{startRow} and #{endRow}

mapper.update:
update employee
set emp_Name=#{empName }, emp_Phone=#{empPhone }
	
	 , emp_gender=#{empGender},  emp_Addr=#{empAddr}
	 , emp_Addr_Detail=#{empAddrDetail }, emp_Post=#{empPost }
	 , position=#{position}, emp_Email=#{empEmail }
	 , emp_birth=#{empBirth}
where emp_NUM = #{empNum}

mapper.select:
select * from ( select rownum rn, section_num, department_name, section_name, section_phone, section_location from section s join department d on s.department_num=d.department_num
where section_num like '%'||#{searchWord}||'%' or section_name like '%'||#{searchWord}||'%' or d.department_num like '%'||#{searchWord}||'%' or department_name like '%'||#{searchWord}||'%' order by section_Num
) where rn between #{startRow} and #{endRow}
