 , patient_gender=#{patientGender},  patient_Addr=#{patientAddr}
	 , patient_Addr_Detail=#{patientAddrDetail }, patient_Post=#{patientPost }
	 , patient_Email=#{patientEmail }, patient_Phone=#{patientPhone },
where patient_NUM = #{patientNum}
